import React from "react";
import DataCourses from "../data/DataCourses";

const Courses = () => {

return(

    <div style={{margin:"10px"}}>
<h2 style={{margin:"15px"}}>My Courses and Projects</h2>
<div>
 {
    DataCourses.map(DATAcourse => { //loop
        return <Project projectPARAM = {DATAcourse}/> //projectPARAM is a parameter to component 
    })
 }
</div>
    </div>
)

};

const Project = (props) =>{ //by putting props inside the function , you will get its properites 
const {title,description,link,image} = props.projectPARAM //here we use the parameter 

    return (

        <div className="card">
    <img src={image} alt="Course" style={{width:"100%"}}/>
<h3 style={{color: "white", fontSize: 20}}>{title}</h3>
<p style={{fontSize: 17 , padding: 4}}>{description}</p>
<button onClick={() =>{
    we.location.href = link;
}
}>Visit</button>
</div>
    );
}
export default Courses;
