import React, { useState } from "react";
import profilePic from "../assest/mns.jpeg";

class Profile extends React.Component {
    
  render() { 
    
    const myStyle = { width: "200px", height: "200px", borderRadius: "100px" };
    const btnStyle = {backgroundColor:"#483d8b", color:"white" , fontSize:"18px"}
    this.state = {displayBio: false};
    toggle = toggle.bind(this)

 function toggle (){
    this.state = this.state
}
    return (
      <div>
        <img src={profilePic} style={myStyle} alt="Profile Picture" />
        <h1>Hello</h1>
        <p>My name is Mansoor Gabali, I am a software engineer.</p>
        {this.state === true ?(
            <div>
        <p>Aspiring in software engineering field, software development and design</p>
        <p>Full-Stack using Spring , React , node.js, sql , Nosql </p>
        <button onClick= {toggle} style={btnStyle}>Hide</button>
        </div>
      
    ):(
        <button onClick= {toggle} style={btnStyle}>Show more</button>

    )}
         
    </div>
    );
  }
}

export default Profile;
