import React from "react";
import CONTACTS_DATA from "../data/ContactsData";


 const SocialContact = () =>{

    return (
  <div style={{textAlign:"center"}}>
    <h2>Contact Me</h2>
   <div>
{ContactsData.map((CONTACTcounter) => (
    <Contact key ={CONTACTcounter.id} contactPARAm={CONTACTcounter} />
)


)}
   </div>
  </div>
    );
 };

 const Contact = (props) => {
    const{link , image , target} = props.contactPARAm;

    return(
<div >
<img src={image} alt="contact" style={{width: "20%" , margin: 4}} />
<a href={link} target={target || "_self"} rel="noopener noreferrer">   </a> 
</div>
    );
 }

 export default SocialContact;
