import React, { Component } from "react";
import profilePic from "../assest/mns.jpeg";

class Profile extends Component {
  constructor() {
    super();
    this.state = { displayBio: false };

    // Bind 'this' to the toggle function
    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    // Correct way to update state
    this.setState((prevState) => ({
      displayBio: !prevState.displayBio
    }));
    console.log("Updated state:", this.state.displayBio); // This might not log immediately due to async state updates
  }

  render() {
    const myStyle = { width: "200px", height: "200px", borderRadius: "100px", paddingTop:"" };
    const btnStyle = { backgroundColor: "#483d8b", color: "white", fontSize: "18px" , borderRadius:"10px" , width: "40%",
      transition: "0.3s" , hover:{ opacity: "0.5"}};

    return (
      <div>
        <img src={profilePic} style={myStyle} alt="Profile Picture" />
        <h1>Hello</h1>
        <p>My name is Mansoor Gabali, I am a software engineer.</p>
        <div className="profile">
        {this.state.displayBio ? (
          <div>
            <p>Aspiring in software engineering, development, and design.</p>
            <p>Full-Stack using Spring, React, Node.js, SQL, NoSQL</p>
            <button onClick={this.toggle} style={btnStyle}>Hide</button>
          </div>
        ) : (
          <button onClick={this.toggle} style={btnStyle}>Show more</button>

        )}
      </div>
      </div>
    );
  }
}

export default Profile;
