import React, { Component } from "react";

const TITLES = [
    "Software Engineer", 
    "AI Trainer", 
    "English Teacher"
];

class Title extends Component {
  constructor() {
    super();
    this.state = {titleIndex:0};
  }
  componentDidMount(){
    this.animateTitles();
  }

  animateTitles (){
    setInterval(()=>{
    const titleIndex = (this.state.titleIndex + 1 )
    this.setState({titleIndex})
    },5000)
  }
  render() {
    return <p>{TITLES[this.state.titleIndex]}</p>;
  }
}

export default Title;
