import React, { useState } from "react";
import ReactCardFlip from "react-card-flip";

const Technologies = () => {
   return (
     <div>
       <h2 className="tech-heading">
         Technologies & Languages That Drive My Development Journey
       </h2>
       <h3 className="tech-heading">Here‚Äôs What‚Äôs Under the Hood üõ†Ô∏è</h3>
       <div className="technologies-container">
         {IMAGES.map((imageObject) => {
           return (
             <Image
               key={imageObject.id}
               src={imageObject.image}
               desc={imageObject.description}
             />
           );
         })}
       </div>
     </div>
   );
 };

const Image = ({ src, desc }) => {
  const [isFlipped, setIsFlipped] = useState(false);

  return (
    <div className="technologies">
      <ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
        <div onClick={() => setIsFlipped(!isFlipped)} className="front-card">
          <img src={src} alt={desc} />
        </div>
        <div onClick={() => setIsFlipped(!isFlipped)} className="back-card">
          <p>{desc}</p>
        </div>
      </ReactCardFlip>
    </div>
  );
};

export default Technologies;
