const Image = ({ src, desc }) => {
   return (
     <div className="technologies" style={{ textAlign: "center", margin: "1rem" }}>
       <a target="_blank" rel="noreferrer" href={src}>
         <img
           src={src}
           alt={desc}
           style={{ width: "100px", height: "100px", objectFit: "contain" }}
         />
       </a>
       <p>{desc}</p>
     </div>
   );
 };
 


 export default Technologies;
