import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import profilePic from "../assest/prof.jpg";

const Profile = () => {
  const [displayBio, setDisplayBio] = useState(false);

  const toggle = () => {
    setDisplayBio(!displayBio);
  };

  const bioVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
  };

  return (
    <div style={{ paddingTop: "10px", margin: "30px", width: "100%" }}>
      <img src={profilePic} className="profile" alt="Profile Picture" />
      <h1 className="hello-text">Hello!</h1>
      <h2
        style={{ paddingBottom: "10px", paddingTop: "30px", margin: "0px", width: "100%" }}
        className="name-text"
      >
        Mansoor Gabali,
      </h2>
      <h4
        style={{ paddingBottom: "40px", paddingTop: "10px", margin: "0px", width: "100%" }}
        className="role-text"
      >
        Software Engineer
      </h4>
      <div>
        <AnimatePresence>
          {displayBio && (
            <motion.div
              initial="hidden"
              animate="visible"
              exit="hidden"
              variants={bioVariants}
            >
              <motion.p variants={bioVariants}>üöÄ Software Engineer | Full-Stack Developer</motion.p>
              <motion.hr variants={bioVariants} />
              <motion.p variants={bioVariants}>
                üí° Passionate about building scalable applications, <br />
                system architecture, and software design.
              </motion.p>
              <motion.hr variants={bioVariants} />
              <motion.p variants={bioVariants}>
                üõ†Ô∏è Expertise: JavaScript (React, Node.js), Java (Spring),<br />
                SQL & NoSQL, AI, and Data Analysis.
              </motion.p>
              <motion.hr variants={bioVariants} />
              <motion.p variants={bioVariants}>
                üîç Focused on: Full-stack development, <br /> software design patterns,
              </motion.p>
              <motion.button
                onClick={toggle}
                className="btn"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                Hide
              </motion.button>
            </motion.div>
          )}
        </AnimatePresence>
        {!displayBio && (
          <motion.button
            onClick={toggle}
            className="btn"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            Show more
          </motion.button>
        )}
      </div>
    </div>
  );
};

export default Profile;
